import React from 'react';

import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';

import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';

import logo from '../res/emblem.svg';

const styles = theme => ({
    Card: {
        margin: 'auto',
        textAlign: 'center',
        minWidth: '270px'
    },
    logoWrapper: {
        position: 'relative'
    },
    logo: {
        width: '70px',
        height: 'auto',
    },
    loginBtn: {
        marginTop: '20px',
        width: '200px'
    },
    forgotPasswordBtn: {
        marginTop: '20px',
        width: '200px'
    },
    textField: {
        width: '100%'
    }
})

let ForgotPasswordComponent = (props) => {
    let { classes } = props;

    return (
        <div className={classes.Card}>
            {/* <CardContent> */}
                <div className={classes.logoWrapper}>
                    <img src={logo} className={classes.logo} alt="Road and Building" />
                </div>
                <div>
                    <TextField
                        id="phoneNo"
                        label="Phone Number"
                        className={classes.textField}
                        margin="normal" />
                </div>
                <div>
                    <TextField
                        id="password"
                        label="Password"
                        type="password"
                        style={{visibility: 'hidden'}}
                        className={classes.textField}
                        margin="small" />
                </div>
                <div>
                    <Button variant="contained" className={classes.loginBtn}>
                        Reset Password
                    </Button>
                </div>
                <div>
                    <Button 
                        variant="flat" 
                        className={classes.forgotPasswordBtn}
                        onClick={props.toLoginTab}
                    >
                        Login
                    </Button>
                </div>
            {/* </CardContent> */}
        </div>
    )
}

ForgotPasswordComponent.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(ForgotPasswordComponent);
