import React, { Component } from 'react';
// import { Route, Switch, Redirect } from 'react-router-dom';

import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import MuiPickersUtilsProvider from 'material-ui-pickers/utils/MuiPickersUtilsProvider';
import DateFnsUtils from 'material-ui-pickers/utils/date-fns-utils';
import DatePicker from 'material-ui-pickers/DatePicker';

import Grid from '@material-ui/core/Grid';
import Divider from '@material-ui/core/Divider';
import FormGroup from '@material-ui/core/FormGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Typography from '@material-ui/core/Typography';
import { Button } from '@material-ui/core';


const styles = theme => ({

})


class SideFilter extends Component {

    // status_type = [
    //     {data:'PENDIND',value:1},
    //     {data:'APPROVED',value:1},
    //     {data:'REJECTED',value:1},
    //     {data:'IN PROGRESS',value:1},
    //     {data:'COMPLETED',value:1}
    // ]

    status_type = [
        'Pending',
        'Approved',
        'In Progress',
        'Completed'
    ]

    // griev_type = [
    //     {data:'DAMAGED BRIDGE',value:1},
    //     {data:'DAMAGED BRIDGE PARAPET',value:1},
    //     {data:'BREACH ON A ROAD',value:1},
    //     {data:'DAMAGED RAILING',value:1},
    //     {data:'SHARP CURVE',value:1},
    //     {data:'ACCIDENT PRONE ZONE',value:1},
    //     {data:'DAMAGE STRUCTURES',value:1},
    //     {data:'POT HOLES',value:1},
    //     {data:'FALLEN TREE',value:1},
    //     {data:'DEGREDED ROADS',value:1},
    //     {data:'CRACKS LEVELING',value:1},
    // ]

    griev_type = [
        'DAMAGED BRIDGE',
        'DAMAGED BRIDGE PARAPET',
        'BREACH ON A ROAD',
        'DAMAGED RAILING',
        'SHARP CURVE',
        'ACCIDENT PRONE ZONE',
        'DAMAGE STRUCTURES',
        'POT HOLES',
        'FALLEN TREE',
        'DEGREDED ROADS',
        'CRACKS LEVELING',
    ]

    officers = [
        {data:'SECTION OFFICER',value:1},
        {data:'DEPUTY EXCECUTIVE ENGINEER',value:1},
        {data:'EXCECUTIVE ENGINEER',value:1},
        {data:'SUPRETENDING ENGINEER',value:1},
        {data:'CHIEF ENGINEER',value:1}
    ]

    state = {
        StartingDate: new Date(),
        EndingDate: new Date(),
        status_type: [...this.status_type],
        griev_type: [...this.griev_type],
        officers: this.officers
    }

    handleStartingDateChange = (date) => {
        this.setState({ StartingDate: date });
    }

    handleEndingDateChange = (date) => {
        this.setState({ EndingDate: date }); 
    }

    handleChange = (e, field) => {
        let value = e.target.value;

        this.setState((prevState, nextState) => {  
            console.log(prevState, nextState);
            if(prevState[field].indexOf(value) > -1) {
                prevState[field].splice(prevState[field].indexOf(value), 1);
                return { [field] : prevState[field] }
            } else {
                prevState[field] = [...prevState[field], value]
                return { [field] : prevState[field] }
            }
        })
    }

    render() {
        // let { classes } = this.props;
        return (
            <div>
                <div style={{margin: '10px', textAlign: 'center'}}>
                    <Typography variant="subheading">Complaint Status</Typography>
                    <br />
                    <Divider />
                    <FormGroup>
                        {
                            this.status_type.map((name, index) => (
                                <FormControlLabel key={index} checked={this.state.status_type.indexOf(name) > -1} value={name} onChange={ e => { this.handleChange(e, 'status_type') } } control={<Checkbox/>} label={name} />
                            ))
                        }
                    </FormGroup>
                </div>
                <div style={{margin: '10px', textAlign: 'center'}}>
                    <Typography variant="subheading">Grievance Type</Typography>
                    <br />
                    <Divider />
                    <FormGroup>
                        {this.griev_type.map((name, index) => (
                            <FormControlLabel key={index} checked={this.state.griev_type.indexOf(name) > -1} control={<Checkbox />} value={name} label={name} onChange={ e => { this.handleChange(e, 'griev_type') } } />
                        ))}
                    </FormGroup>
                </div>
                <div style={{margin: '10px',}}>
                    <Typography variant="subheading" style={{textAlign: 'center'}}>Officers</Typography>
                    <br />
                    <Divider />
                    <FormGroup>
                        {this.officers.map((name, index) => (
                            <FormControlLabel key={index} checked={this.state.officers.value} control={<Checkbox />} label={name.data} />
                        ))}
                    </FormGroup>
                </div>
                <div style={{margin: '10px'}}>
                    <Typography variant="subheading" style={{textAlign: 'center'}}>Date</Typography>
                    <br />
                    <Divider />
                    <br />
                    <MuiPickersUtilsProvider utils={DateFnsUtils}>
                        <Typography variant="caption">Starting Date</Typography>
                        <DatePicker 
                            value={this.state.EndingDate}
                            onChange={this.handleEndingDateChange}
                        />
                    </MuiPickersUtilsProvider>
                    <br /><br />
                    <MuiPickersUtilsProvider utils={DateFnsUtils}>
                        <Typography variant="caption">Ending Date</Typography>
                        <DatePicker
                            value={this.state.StartingDate}
                            onChange={this.handleStartingDateChange}
                        />
                    </MuiPickersUtilsProvider>
                </div>
                <Divider />
                <div style={{margin: '10px', textAlign: 'center'}}>
                    <Grid container>
                            <Grid item xs>
                                <Button color="primary">apply</Button>
                            </Grid>
                            <Grid item xs>
                                <Button color="secondary">reset</Button>
                            </Grid>
                    </Grid>
                </div>
            </div>
        )
    }
}

SideFilter.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(SideFilter);
